mapper.insert:

INSERT INTO tb_board (
	seq
	, parent_seq
	, category
	, title
	, contents
	, writer
	, write_date
	, hits
	, reply_yn
	, del_yn
) VALUES (
	(SELECT a.maxSeq from (SELECT MAX(seq)+1 as maxSeq FROM tb_board) a ) 
	, 0
	, #{category}
	, #{title}
	, #{contents}
	, #{writer}
	, now()
	, 0
	, 'N'
	, 'N'
	);
